mapper.select:
SELECT COUNT(IF(app_name="티몬", app_name, NULL)) AS tmon, COUNT(IF(app_name="티몬플러스", app_name, NULL)) AS tmonplus, COUNT(IF(app_name="쿠팡", app_name, NULL)) AS coupang, COUNT(IF(app_name="위메프", app_name, NULL)) AS wemap
FROM review WHERE
DATE >= #{startDate} AND DATE <= #{endDate}

SELECT AVG(IF(app_name="티몬", rating, NULL)) AS tmon_rating, AVG(IF(app_name="위메프", rating, NULL)) AS wemap_rating, AVG(IF(app_name="쿠팡", rating, NULL)) AS coupang_rating
FROM review WHERE
DATE >= #{startDate} AND DATE <= #{endDate}
